Analytics.Script:
CREATE TEMPORARY TABLE activeSessionTable USING CarbonAnalytics OPTIONS (tableName "ACTIVE_SESSIONS", schema "sessionId STRING, startTimestamp LONG, renewTimestamp LONG, terminationTimestamp LONG, action INT, userName STRING, userstoreDomain STRING, IP STRING, tenantDomain STRING, rememberMeFlag BOOLEAN", primaryKeys "sessionId");

CREATE TEMPORARY TABLE rawSessionsTable USING CarbonAnalytics OPTIONS (tableName "SESSION-INFO-STREAM");

INSERT INTO TABLE rawSessionsTable SELECT sessionId, startTimestamp, renewTimestamp, terminationTimestamp, -1, now(0) - startTimestamp, true, userName, userstoreDomain, IP, tenantDomain, rememberMeFlag from activeSessionTable where terminationTimestamp > now(0);

CREATE TEMPORARY TABLE sesssionEventsNotificationTable USING org.wso2.carbon.analytics.spark.event.EventStreamProvider OPTIONS (streamName "session-analytics-stream", version "1.0.0", payload "sessionId STRING, startTimestamp LONG, renewTimestamp LONG, terminationTimestamp LONG, action INT, userName STRING, userstoreDomain STRING, IP STRING, tenantDomain STRING, rememberMeFlag BOOLEAN, _timestamp LONG");

INSERT INTO TABLE sesssionEventsNotificationTable SELECT sessionId, startTimestamp, renewTimestamp, terminationTimestamp, 0, userName, userstoreDomain, IP, tenantDomain, rememberMeFlag, terminationTimestamp from activeSessionTable WHERE terminationTimestamp < now(0);
